import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [originalImage, setOriginalImage] = useState(null);
  const [imageURL, setImageURL] = useState(null);
  const [operation, setOperation] = useState('');

  // Resim yükleme
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setOriginalImage(file); // Orijinal resmi kaydediyoruz
    setImageURL(URL.createObjectURL(file));
  };

  // Resmi işleme
  const handleProcessImage = async () => {
    const formData = new FormData();
    formData.append('image', image);
    formData.append('operation', operation);

    try {
      const response = await axios.post('http://127.0.0.1:5000/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        responseType: 'blob',
      });
      const url = URL.createObjectURL(response.data);
      setImageURL(url); // İşlenmiş resmin URL'sini güncelle
    } catch (error) {
      console.error("Bir hata oluştu:", error);
    }
  };

  // Orijinal resme geri dönme
  const handleResetImage = () => {
    setImageURL(URL.createObjectURL(originalImage));
  };

  return (
    <div className="App">
      <h1>Görsel İşleme Uygulaması</h1>

      <input type="file" onChange={handleImageChange} />

      {imageURL && (
        <div>
          <h3>İşlenmiş Resim</h3>
          <img src={imageURL} alt="Processed" style={{ width: '100%', height: 'auto' }} />
        </div>
      )}

      <div>
        <button onClick={() => setOperation('negative')}>Negatif</button>
        <button onClick={() => setOperation('grayscale')}>Grayscale</button>
        <button onClick={() => setOperation('brightness')}>Parlaklık</button>
        <button onClick={() => setOperation('red')}>Kırmızı</button>
        <button onClick={() => setOperation('blue')}>Mavi</button>
        <button onClick={() => setOperation('green')}>Yeşil</button>
        <button onClick={() => setOperation('rectangle')}>Dikdörtgen</button>
        <button onClick={() => setOperation('circle')}>Daire</button>
        <button onClick={() => setOperation('ellipse')}>Elips</button>
        <button onClick={() => setOperation('polygon')}>Çokgen</button>
      </div>

      {image && (
        <button onClick={handleProcessImage}>İşlemi Uygula</button>
      )}

      {image && (
        <button onClick={handleResetImage}>Orijinal Resme Dön</button>
      )}
    </div>
  );
}

export default App;
